query SearchTGX($criteria: HotelCriteriaSearchInput!, $settings: HotelSettingsInput!, $filter: HotelXFilterSearchInput) {
  hotelX {
    search(criteria: $criteria, settings: $settings, filterSearch: $filter) {
      context
      options {
        id
        accessCode
        supplierCode
        hotelCode
        hotelName
        boardCode
        paymentType
        status
        occupancies {
          id
          paxes {
            age
          }
        }
        rooms {
          occupancyRefId
          code
          description
          refundable
          roomPrice {
            price {
              currency
              binding
              net
              gross
              exchange {
                currency
                rate
              }
            }
            breakdown {
              start
              end
              price {
                currency
                binding
                net
                gross
                exchange {
                  currency
                  rate
                }
                minimumSellingPrice
              }
            }
          }
          beds {
            type
            count
          }
          ratePlans {
            start
            end
            code
            name
          }
          promotions {
            start
            end
            code
            name
          }
        }
        price {
          currency
          binding
          net
          gross
          exchange {
            currency
            rate
          }
          minimumSellingPrice
          markups {
            channel
            currency
            binding
            net
            gross
            exchange {
              currency
              rate
            }
            rules {
              id
              name
              type
              value
            }
          }
        }
        supplements {
          start
          end
          code
          name
          description
          supplementType
          chargeType
          mandatory
          durationType
          quantity
          unit
          resort {
            code
            name
            description
          }
          price {
            currency
            binding
            net
            gross
            exchange {
              currency
              rate
            }
          }
        }
        surcharges {
          code
          chargeType
          description
          mandatory
          price {
            currency
            binding
            net
            gross
            exchange {
              currency
              rate
            }
            markups {
              channel
              currency
              binding
              net
              gross
              exchange {
                currency
                rate
              }
            }
          }
        }
        rateRules
        cancelPolicy {
          refundable
          cancelPenalties {
            deadline
            isCalculatedDeadline
            penaltyType
            currency
            value
          }
        }
        remarks
      }
      errors {
        code
        type
        description
      }
      warnings {
        code
        type
        description
      }
    }
  }
}
