query:
mutation ($input: HotelBookInput!, $settings: HotelSettingsInput!) {
  hotelX {
    book(input: $input, settings: $settings) {
      booking {
        status
        reference { bookingID client supplier hotel }

price {
  currency
  net
  gross
  binding
}

        holder { name surname }
        cancelPolicy {
          refundable
          cancelPenalties {
            deadline
            isCalculatedDeadline
            penaltyType
            currency
            value
          }
        }
        hotel {
          hotelCode
          hotelName
          bookingDate
          start
          end
          boardCode
          occupancies {
            id
            paxes { age }
          }
          rooms {
            code
            description
            occupancyRefId
            price {
              currency
              binding
              net
              gross
              exchange { currency rate }
            }
          }
        }
        remarks
      }
      errors   { code type description }
      warnings { code type description }
    }
  }
}
